# making a new array without the @
def five_characters_name(new_journalists_array)
  # finding the length of 5 caracters
  puts "Les journalistes ayant un nom de 5 lettres sont les suivants :"

  new_journalists_array.each do |s|
      if s.length == 5
        puts s
      else
      end
  end
end

five_characters_name(new_journalists_array)

puts

# showing the names with capital letters
